// N8N Node Code untuk parsing WooCommerce Order ke Discord

// Debug: Cek struktur data yang masuk
console.log('Full $json:', JSON.stringify($json, null, 2));

// Coba berbagai kemungkinan struktur data
let orderData;

if ($json && $json.body) {
  // Jika data langsung di $json.body
  orderData = $json.body;
} else if ($json && Array.isArray($json) && $json[0] && $json[0].body) {
  // Jika data di $json[0].body
  orderData = $json[0].body;
} else if ($json && Array.isArray($json) && $json[0]) {
  // Jika data langsung di $json[0]
  orderData = $json[0];
} else if ($json) {
  // Jika data langsung di $json
  orderData = $json;
} else {
  throw new Error('Data webhook tidak ditemukan. Struktur data: ' + JSON.stringify($json));
}

// Validasi apakah orderData memiliki field yang dibutuhkan
if (!orderData || !orderData.id) {
  throw new Error('Data pesanan tidak valid. OrderData: ' + JSON.stringify(orderData));
}

// Parse informasi pesanan utama
const orderInfo = {
  id: orderData.id,
  status: orderData.status,
  total: orderData.total,
  currency: orderData.currency,
  currency_symbol: orderData.currency_symbol,
  date_created: orderData.date_created,
  payment_method: orderData.payment_method_title,
  shipping_total: orderData.shipping_total
};

// Parse informasi customer
const customerInfo = {
  name: `${orderData.billing.first_name} ${orderData.billing.last_name}`,
  email: orderData.billing.email,
  phone: orderData.billing.phone,
  address: `${orderData.billing.address_1}${orderData.billing.address_2 ? ', ' + orderData.billing.address_2 : ''}, ${orderData.billing.city}, ${orderData.billing.state} ${orderData.billing.postcode}`,
  country: orderData.billing.country
};

// Parse item-item yang dibeli (dinamis)
const items = orderData.line_items.map(item => ({
  name: item.name,
  sku: item.sku,
  quantity: item.quantity,
  price: item.price,
  subtotal: item.subtotal,
  product_id: item.product_id,
  image_url: item.image?.src || null
}));

// Parse informasi pengiriman
const shippingInfo = orderData.shipping_lines.length > 0 ? {
  method: orderData.shipping_lines[0].method_title,
  cost: orderData.shipping_lines[0].total
} : null;

// Format currency untuk display
const formatCurrency = (amount) => {
  return new Intl.NumberFormat('id-ID', {
    style: 'currency',
    currency: orderData.currency,
    minimumFractionDigits: 0
  }).format(amount);
};

// Buat embed untuk Discord
const discordEmbed = {
  title: `ðŸ›’ Pesanan Baru #${orderInfo.id}`,
  color: orderInfo.status === 'pending' ? 16776960 : // Kuning untuk pending
         orderInfo.status === 'processing' ? 255 : // Biru untuk processing  
         orderInfo.status === 'completed' ? 65280 : // Hijau untuk completed
         16711680, // Merah untuk status lainnya
  timestamp: new Date(orderInfo.date_created).toISOString(),
  fields: [
    {
      name: "ðŸ“Š Status Pesanan",
      value: orderInfo.status.toUpperCase(),
      inline: true
    },
    {
      name: "ðŸ’° Total Pembayaran", 
      value: formatCurrency(orderInfo.total),
      inline: true
    },
    {
      name: "ðŸ’³ Metode Pembayaran",
      value: orderInfo.payment_method,
      inline: true
    },
    {
      name: "ðŸ‘¤ Customer",
      value: `**Nama:** ${customerInfo.name}\n**Email:** ${customerInfo.email}\n**Telepon:** ${customerInfo.phone}`,
      inline: false
    },
    {
      name: "ðŸ“ Alamat Pengiriman",
      value: customerInfo.address,
      inline: false
    }
  ]
};

// Tambahkan field untuk setiap item yang dibeli
items.forEach((item, index) => {
  discordEmbed.fields.push({
    name: `ðŸ›ï¸ Item ${index + 1}`,
    value: `**${item.name}**\n` +
           `SKU: ${item.sku}\n` +
           `Qty: ${item.quantity}x\n` +
           `Harga: ${formatCurrency(item.price)}\n` +
           `Subtotal: ${formatCurrency(item.subtotal)}`,
    inline: true
  });
});

// Tambahkan info pengiriman jika ada
if (shippingInfo) {
  discordEmbed.fields.push({
    name: "ðŸšš Pengiriman",
    value: `${shippingInfo.method}\nBiaya: ${formatCurrency(shippingInfo.cost)}`,
    inline: true
  });
}

// Tambahkan thumbnail dari produk pertama jika ada gambar
if (items.length > 0 && items[0].image_url) {
  discordEmbed.thumbnail = {
    url: items[0].image_url
  };
}

// Footer dengan informasi tambahan
discordEmbed.footer = {
  text: `Order ID: ${orderInfo.id} | Taffware Store`,
  icon_url: "https://taffware.com/wp-content/uploads/2024/03/cropped-logo-taffware-32x32.png"
};

// Return data untuk Discord Bot (format lebih sederhana)
return [
  {
    json: {
      // Kirim sebagai single embed object, bukan array embeds
      title: discordEmbed.title,
      color: discordEmbed.color,
      description: `**Customer:** ${customerInfo.name}\n**Email:** ${customerInfo.email}\n**Total:** ${formatCurrency(orderInfo.total)}`,
      fields: discordEmbed.fields.slice(0, 10), // Limit fields untuk avoid error
      timestamp: discordEmbed.timestamp,
      footer: discordEmbed.footer,
      thumbnail: discordEmbed.thumbnail
    }
  }
];

// Alternative: Jika ingin format text biasa tanpa embed
